import React, {useState} from "react";
import MobileMenu from "./MobileMenu";
import { logo, menu, cart, user, wishlist, searchIcon } from "./Index";

const Header = () => {
  const [isMobileSite, setIsMobileSite] = useState(true); // Change this to your actual logic

  const handleMenuClick = () => {
    // Toggle mobile menu visibility
    setIsMobileSite(!isMobileSite);
  };

  return (
    <nav className="bg-whit p-4 relative">
    {/* Logo */}
    <div className="w-32 h-14 left-208 top-24 absolute">
    <img
    className="w-32 h-7 left-0 top-15.47 absolute"
    src={logo}
    alt="Logo"
  />
    </div>
    {/* Search Bar */}
    <div className="w-96 h-12 px-2 left-420 top-30 absolute">
      {/* Your search input and search icon */}
    </div>
    {/* Menu Icon */}
    <button onClick={handleMenuClick} className="w-8 h-8 left-[420px] top-[30px] absolute focus:outline-none">
      {/* Your menu icon image */}
    </button>
    {/* Mobile Menu */}
    {isMobileSite && <MobileMenu />}
    {/* Other elements (SignIn, Wishlist, Cart) */}
  </nav>
);
};

export default Header;
