import React, {useState} from "react";
import MobileMenu from "./MobileMenu";
import { logo, menu, cart, user, wishlist, searchIcon } from "./Index";

const Header = () => {


  return (
    <nav className="bg-whit p-4 relative flex flex-col md:flex-row md:items-center">
    {/* Logo */}
    <div className="w-32 h-14 left-208 top-24 absolute">
    <img
    className="w-32 h-7 left-0 top-15.47 absolute"
    src={logo}
    alt="Logo"
  />
    </div>
    {/* Search Bar */}
    <div className="w-96 h-12 px-2 left-420 top-30 absolute">
    <input
    type="text"
    name="query"
    placeholder="Search..."
    className="w-full h-full pl-10 pr-4 py-2 bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-green-700 focus:border-green-700"
  />
  <div className="w-6 h-6 pr-10 left-24 top-12 ">
    <div className="w-6 h-6 left-0 top-0 ">
      <img
        className="w-4 h-4 left-0 top-3 "
        src={searchIcon}
        alt="searchIcon"
      />
    </div>
  </div>
    </div>
    {/* Menu Icon */}
    <button onClick={handleMenuClick} className="w-8 h-8 left-[420px] top-[30px] absolute focus:outline-none">
    <img
      className="w-5 h-5 left-[5.33px] top-[6.67px] absolute"
      src={menu}
      alt="Menu"
    />
    </button>
    {/* Mobile Menu */}
    {isMobileSite && <MobileMenu />}
    {/* Other elements (SignIn, Wishlist, Cart) */}
  </nav>
);
};

export default Header;
