import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { toggleDropdown } from "./reducer/dropdownSlice";
import NavbarItem from "./NavbarItem";

import { logo, menu, cart, user, wishlist, searchIcon } from "./Index";


const Header = () => {

  const dispatch = useDispatch();

 const  isDropdownOpen = useSelector(state => state);

 const   toggleMenu = () => {
    dispatch(toggleDropdown());
  }
  const handleLogoClick = () => {
    console.log("Logo clicked!");

  };
  return (
    <nav className="flex flex-col md:p-4 md:items-center md:w-auto bg-white border-gray-200 dark:bg-gray-900">
      <div className="flex flex-reverse md:relative flex-col p-4 items-center justify-between">

        <a href="/dashboard" className="md:w-200 md:h-50 items-center justify-center ">

          <img
            className="md:w-200 md:h-50 items-center justify-center "
            src={logo}
            alt="Logo"
          />

        </a>
      </div>
      {/* <div className="flex items-center ">
        // <img
        //   className=" w-32 h-7 left-0 top-[15.47px] "
        //   src={menu}
        //   alt="Menu"
        //   onClick={handleLogoClick}
        // />
      </div> */}
      {/* <div className="flex flex-grow md:flex-col md:items-center justify-between"> */}




       <div className="max-w-screen-xl flex flex-wrap items-center justify-between mx-auto p-4">
       {/* Use the NavbarItem component for each item */}
       {/* <NavbarItem href="/user" icon={user} text="SignIn" />
       <NavbarItem href="/favorite" icon={wishlist} text="Wishlist" />
       <NavbarItem href="/cart" icon={cart} text="$12.21" /> */}
          <div className="flex-reverse  flex-grow md:items-center    md:w-auto relative ">
          <input
            type="text"
            name="query"
            placeholder="Search..."
            className=" py-2 w-full  md:w-auto bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-green-700 focus:border-green-700 pl-8 pr-3" // Added padding for icon
          />

          <div className="absolute inset-y-0 left-0 pl-2 flex items-center pointer-events-none">
            <img
              className="w-4 h-4 text-gray-400"
              src={searchIcon}
              alt="Search Icon"
            />
          </div>
        </div>
       <button data-collapse-toggle="navbar-default" type="button" className="inline-flex items-center p-1 w-10 h-10 justify-center text-sm text-gray-500 rounded-lg md:hidden hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-gray-200 dark:text-gray-400 dark:hover:bg-gray-700 dark:focus:ring-gray-600" aria-controls="navbar-default" aria-expanded="false">
        <span className="sr-only">Open main menu</span>
        <img
          className=" w-32 h-7 left-0 top-[15.47px] "
          src={menu}
          alt="Menu"
          onClick={handleLogoClick}
        />
    </button>
    <div className="hidden w-full  md:block md:w-auto" id="navbar-default">
      <ul className="font-medium flex flex-col p-4  md:p-0 mt-4  border border-gray-100 rounded-lg bg-gray-50 md:flex-row md:space-x-8 md:mt-0 md:border-0 md:bg-white dark:bg-gray-800 md:dark:bg-gray-900 dark:border-gray-700">
        <li>
        <NavbarItem href="/user" icon={user} text="SignIn" />
          {/* <a href="#" className="block py-2 pl-3 pr-4 text-white bg-blue-700 rounded md:bg-transparent md:text-blue-700 md:p-0 dark:text-white md:dark:text-blue-500" aria-current="page">Home</a> */}
        </li>
        <li>
        <NavbarItem href="/favorite" icon={wishlist} text="Wishlist" />          {/* <a href="#" className="block py-2 pl-3 pr-4 text-gray-900 rounded hover:bg-gray-100 md:hover:bg-transparent md:border-0 md:hover:text-blue-700 md:p-0 dark:text-white md:dark:hover:text-blue-500 dark:hover:bg-gray-700 dark:hover:text-white md:dark:hover:bg-transparent">About</a> */}
        </li>

        <li>
        <NavbarItem href="/cart" icon={cart} text="$12.21" />
          </li>

      </ul>
    </div>
     </div>


    {/* </div> */}

    </nav>
  );
};

export default Header;
